import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';



function App() {
  const [cards, setValue] = useState([
  [{id:"1", color: "color1", state: 2, left: 0, right: 0, top: 0, bottom: 0},
  {id:"2", color: "color1", state: 2, left: 0, right: 0, top: 0, bottom: 0},
  {id:"3", color: "color2", state: 2, left: 0, right: 0, top: 0, bottom: 0},
  {id:"4", color: "color2", state: 2, left: 0, right: 0, top: 0, bottom: 0},],
  [{id:"5", color: "color3", state: 2, left: 0, right: 0, top: 0, bottom: 0},
  {id:"6", color: "color3", state: 2, left: 0, right: 0, top: 0, bottom: 0},
  {id:"7", color: "color4", state: 2, left: 0, right: 0, top: 0, bottom: 0},
  {id:"8", color: "color4", state: 2, left: 0, right: 0, top: 0, bottom: 0},],
  [{id:"9", color: "color5", state: 2, left: 0, right: 0, top: 0, bottom: 0},
  {id:"10", color: "color5", state: 2, left: 0, right: 0, top: 0, bottom: 0},
  {id:"11", color: "color6", state: 2, left: 0, right: 0, top: 0, bottom: 0},
  {id:"12", color: "color6", state: 2, left: 0, right: 0, top: 0, bottom: 0},],
]);

const drawCards = () => 
{    
    let x = 50;
    let y = 50;

    for (let i = 0; i < 3; i++) 
    {
        for (let j = 0; j < 4; j++) 
        {
            let card = cards[i][j];
            if (card.state != 0)
            {
                card.left = x;
                card.top = y;
                card.right = x + 70;
                card.bottom = y + 100;
                //char* imagePath = NULL;
                // if (card.state == faceUp)
                //     imagePath = path[card.color];
                // else 
                //     imagePath = path[0];

                // drawBitmap(hdc, imagePath, card.left, card.top, 0, 0);
            }

            x += 70 + 20;
        }
        x = 50;
        y += 100 + 20;
    }
}

const generateLevel = () =>
{
  const getRandomInt = (max) => {
    return Math.floor(Math.random() * max);
  }

    // Алгоритм Фишера-Йетса
    for (let i = 0; i < 3; i++)
    {
        for (let j = 0; j < 4; j++)
        {
            let ri = getRandomInt(i + 1);
            let rj = getRandomInt(j + 1);

            let tmp = cards[ri][rj];
            cards[ri][rj] = cards[i][j];
            cards[i][j] = tmp;
        }
    }
}


  const handleClick = (event) => 
  {
    generateLevel();
    drawCards();
    for (let row of cards) {
      for (let value of row) {
        console.log(value.color, value.state, value.left, value.right, value.top, value.bottom);
        }
      }
  } 

  function NumberList(props) {
    const numbers = props.numbers;
    const listItems = numbers.map((number) =>
      <li key={number.id}>
        {number}
      </li>
    );
    return (
      <ul>{listItems}</ul>
    );
  }

  // const listItems = cards.map((item) => 
  // {
  // return <img key={item.id} src="./images/back.bmp" height="100px" width="70px"/>
  // }

  return (
    <div className="App">
      <header className="App-header">
      {<button onClick={handleClick}>Button</button>}
      <NumberList numbers={cards} />
      </header>
    </div>
  );
}

export default App;
