import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';
import imgSrc from './images/back.bmp';

function App() {
  const [cards, setValue] = useState([
  {id: 1, color: "color1", state: 2},
  {id: 2, color: "color1", state: 2},
  {id: 3, color: "color2", state: 2},
  {id: 4, color: "color2", state: 2},
  {id: 5, color: "color3", state: 2},
  {id: 6, color: "color3", state: 2},
  {id: 7, color: "color4", state: 2},
  {id: 8, color: "color4", state: 2},
  {id: 9, color: "color5", state: 2},
  {id: 10, color: "color5", state: 2},
  {id: 11, color: "color6", state: 2},
  {id: 12, color: "color6", state: 2},
]);

React.useEffect(() => {    
  console.log("useEffect");
  generateLevel();
},
[cards]); 

const generateLevel = () =>
{
  const getRandomInt = (max) => {
    return Math.floor(Math.random() * max);
  }

  // Алгоритм Фишера-Йетса
  for (let i = 0; i < 11; i++)
  {
    let ri = getRandomInt(i + 1);
    let tmp = cards[ri];
    cards[ri] = cards[i];
    cards[i] = tmp;
  }
}


  const handleClick = (event) => 
  {

    for (let value of cards) {
        console.log(value.id, value.color, value.state);
      }
  } 

  const imageClick = (card) => {
    console.log('Click:', card.id, card.color);
  } 

  const listItems = cards.map(card =>
    <li key={card.id}>
      <img
        src={imgSrc} height="100px" width="70px"
        onClick={() => imageClick(card)}
      />
    </li>
  );

  return (
    <div className="App">
      <header className="App-header">
      {<button onClick={handleClick}>Button</button>}
      <ul className="App-cards">{listItems}</ul>
      </header>
    </div>
  );
}

export default App;
